mapper.select:
select * from poll_factory WHERE
TJNF LIKE CONCAT('%', #{map.tjnf},'%') and id LIKE CONCAT('%', #{map.id},'%') and XZQHMC LIKE CONCAT('%', #{map.xzqhmc},'%') and ZZJGDM LIKE CONCAT('%', #{map.zzjgdm},'%') and DWMC_GZ LIKE CONCAT('%', #{map.dwmcGz},'%') and SSDM LIKE CONCAT('%', #{map.ssdm},'%') and SSKZDY LIKE CONCAT('%', #{map.sskzdy},'%') and QYXXDZ_DQ_S_Z_M LIKE CONCAT('%', #{map.qyxxdzDqSZM},'%') and QYXXDZ_X_Q_S_Q LIKE CONCAT('%', #{map.qyxxdzXQSQ},'%') and QYXXDZ_X_Z LIKE CONCAT('%', #{map.qyxxdzXZ},'%') and QYXXDZ_J_C_MPH LIKE CONCAT('%', #{map.qyxxdzJCMph},'%') and JD LIKE CONCAT('%', #{map.jd},'%') and WD LIKE CONCAT('%', #{map.wd},'%') and WSCLFFMC1 LIKE CONCAT('%', #{map.wsclffmc1},'%') and PSQXLXMC LIKE CONCAT('%', #{map.psqxlxmc},'%') and SNSTMC LIKE CONCAT('%', #{map.snstmc},'%') and YXTS_T LIKE CONCAT('%', #{map.yxtsT},'%') and WSSJCLNL_D_R LIKE CONCAT('%', #{map.wssjclnlDR},'%') and WSSJCLL_WD LIKE CONCAT('%', #{map.wssjcllWd},'%') and QZ_CLSHWSL_WD LIKE CONCAT('%', #{map.qzClshwslWd},'%') and QZ_CLGYFSL_WD LIKE CONCAT('%', #{map.qzClgyfslWd},'%') and HXXYLQCL_D LIKE CONCAT('%', #{map.hxxylqclD},'%') and HXXYLJKND_HK_S LIKE CONCAT('%', #{map.hxxyljkndHkS},'%') and HXXYLCKND_HK_S LIKE CONCAT('%', #{map.hxxylckndHkS},'%') and ADQCL_D LIKE CONCAT('%', #{map.adqclD},'%') and ADJKND_HK_S LIKE CONCAT('%', #{map.adjkndHkS},'%') and ADCKND_HK_S LIKE CONCAT('%', #{map.adckndHkS},'%') and ZDQCL_D LIKE CONCAT('%', #{map.zdqclD},'%') and ZDJKND_HK_S LIKE CONCAT('%', #{map.zdjkndHkS},'%') and ZDCKND_HK_S LIKE CONCAT('%', #{map.zdckndHkS},'%') and ZLQCL_QK LIKE CONCAT('%', #{map.zlqclQk},'%') and ZLJKND_HK_S LIKE CONCAT('%', #{map.zljkndHkS},'%') and ZLCKND_HK_S LIKE CONCAT('%', #{map.zlckndHkS},'%') and is_del = 0
ORDER BY XZQHMC limit #{startPos},#{pageSize}
